mapper.delete:
	/*체크된 컵 삭제*/
		DELETE FROM user_manage
		WHERE
			user_Id IN
			
    	
      		#{item}

mapper.update:
/* 상품수정 */ UPDATE user_manage AS u
u.static_code = #{staticCode},
u.admin_id = #{userId},
u.user_join_datetime =NOW()
WHERE u.user_id =#{userId};

mapper.select:
/* 회원목록 조회 */ SELECT u.user_id AS userId, u.user_pw AS userPw, u.user_name AS userName, u.user_level AS userLevel, u.user_Idcard_number AS userIdcardnumber, u.user_addr AS userAddr, u.user_email AS userEmail, u.user_phone AS userPhone, u.user_nick AS userNick, u.user_sms_check AS userSmscheck, u.user_email_check AS userEmailcheck, u.static_code AS staticCode, cast(u.user_join_datetime AS CHAR(20)) AS userJoindatetime FROM user_manage as u
${searchKey} = #{searchValue}
LIMIT #{startIndex},#{rowPerPage};

		/*회원 전체 행의 갯수*/
		SELECT
			COUNT(*)
		FROM
			user_manage as u
		
        	
        		${searchKey} = #{searchValue};
