mapper.insert:
/* 회원가입 */ INSERT INTO cup_discard_manage VALUES
((SELECT cup_discard_manage_code FROM (SELECT CASE WHEN COUNT(*) = 0 THEN 'cup_discard_manage_code1' ELSE CONCAT('cup_discard_manage_code', MAX(CAST(SUBSTRING_INDEX(cup_discard_manage_code,'code', -1) AS UNSIGNED))+1)
END AS cup_discard_manage_code FROM cup_discard_manage)sub_sc), #{cup.partnerInfo}, #{cup.cupQR}, 'adminid001', CURDATE())

mapper.select:
/*폐기컵 조회*/ SELECT CAST(SUBSTRING_INDEX(cdm.cup_discard_manage_code,'code',-1) AS UNSIGNED) AS num, cdm.approval_partner_plus_info_code AS partnerInfo, cdm.cup_QR_code AS cupQR, cdm.admin_id AS adminName, cdm.up_datetime AS upDateTime FROM cup_discard_manage AS cdm
ORDER BY CAST(SUBSTRING_INDEX(cdm.cup_discard_manage_code,'code',-1) AS UNSIGNED)
LIMIT #{startIndex},#{rowPerPage};

/*컵 상태의 전체 행의 갯수*/
SELECT
	COUNT(*)
FROM
	cup_discard_manage;

mapper.update:
/* 상품수정 */ UPDATE cup_manage AS cm
cm.static_code = #{staticCode},
cm.admin_id = #{adminName},
cm.up_datetime =NOW()
WHERE cm.cup_QR_code =#{cupQR};

mapper.select:
/*재고 전체 행의 갯수*/
SELECT
	COUNT(*)
FROM
	cup_stack_manage;

