mapper.delete:
/* 기준삭제 */
DELETE
FROM
waiting_deposit_standard
WHERE
admin_id = #{adminId} and waiting_deposit_standard_code= #{waitingDepositStandardCode} ;

mapper.insert:
/* 기준 등록 */
INSERT
  INTO waiting_deposit_standard
VALUES (
		#{waitingDepositStandardCode}
      , #{waitingDepositPeriod}
      , NOW()
      , #{adminId}
      , NOW()
		)

mapper.update:
/* 보증금 수정 */ UPDATE waiting_deposit_standard
waiting_deposit_period = #{waitingDepositPeriod},
set_datetime = #{setDatetime},
admin_id = #{adminId},
up_datetime = NOW()
WHERE admin_id = #{adminId}
