mapper.update:
/* 1-4 하루 최대 적립 포인트 횟수 기준 수정 */ UPDATE tb_goods
g_name = #{goodsName},
g_price = #{goodsPrice},
g_seller_id = #{goodsSellerId},
WHERE g_code = #{goodsCode}

mapper.insert:
/* 등급명과 일치하는 등급 코드 조회 */
SELECT point_save_use_type_code AS pointSaveUseTypeCode FROM point_save_use_type WHERE point_save_use_type = #{pointType};
/* 5-2 포인트 환급 기준 등록 */ INSERT INTO point_refund_standard (point_refund_standard_code, point_save_use_type_code, minimum_refund_standard, refund_unit, refund_fee_rate, code_use, set_datetime, admin_id, up_datetime) VALUES (#{newCode}, #{pointSaveUseTypeCode}, #{minRefundPoint}, #{refundUnit}, #{refundFeeRate}, #{codeUse}, CURDATE(), #{adminId}, CURDATE())

/* 등급명과 일치하는 등급 코드 조회 */
SELECT grade_standard_code AS gradeStandardCode FROM grade_standard WHERE grade_name = #{gradeName};
/* 4-2 포인트 적립 기준 등록 */ INSERT INTO point_save_standard (point_save_standard_code, grade_standard_code, grade_attain_point, use_save_point, code_use, set_datetime, admin_id, up_datetime) VALUES (#{newCode}, #{gradeStandardCode}, #{gradePoint}, #{usePoint}, #{codeUse}, CURDATE(), #{adminId}, CURDATE())

mapper.select:
/* 컬럼 내 중복 제거된 데이터 조회 */
SELECT
	
	DISTINCT ${columnName} AS distinctData
	
	
	
		DISTINCT CASE 
		   WHEN code_use = 'Y' THEN '사용중'
		   WHEN code_use = 'N' THEN '사용중지'
		END  AS distinctData
	
FROM
	${tableName}

	
		day_maximum_count_code LIKE'%point%';
