mapper.update:
/* 1-4 하루 최대 적립 포인트 횟수 기준 수정 */ UPDATE tb_goods
g_name = #{goodsName},
g_price = #{goodsPrice},
g_seller_id = #{goodsSellerId},
WHERE g_code = #{goodsCode}

mapper.select:
/* 데이터 개수 조회 */
SELECT
	COUNT(*)
FROM
	${tableName}

	
		day_maximum_count_code LIKE'%point%';

/* 컬럼 내 중복 제거된 데이터 조회 */
SELECT
	
	DISTINCT ${columnName} AS distinctData
	
	
	
		DISTINCT CASE 
		   WHEN code_use = 'Y' THEN '사용가능'
		   WHEN code_use = 'N' THEN '사용불가능'
		END  AS distinctData
	
FROM
	${tableName}

	
		day_maximum_count_code LIKE'%point%';
