mapper.update:
/* 1-4 하루 최대 적립 포인트 횟수 기준 수정 */ UPDATE tb_goods
g_name = #{goodsName},
g_price = #{goodsPrice},
g_seller_id = #{goodsSellerId},
WHERE g_code = #{goodsCode}

mapper.select:
/* 1-2 하루 최대 적립 포인트 횟수 기준 조회 */
day_maximum_count_code AS '횟수 기준 코드',
use_maximum_count AS '적립 가능 횟수', CASE WHEN code_use = 'Y' THEN '사용가능' WHEN code_use = 'N' THEN '사용불가능' END AS '코드 사용 유무',
set_datetime AS '최초 등록일', admin_id AS '관리자 ID', up_datetime AS '최종 수정일'
FROM day_maximum_count WHERE day_maximum_count_code LIKE CONCAT('%', #{type}, '%') ORDER BY ROW_NUMBER() OVER (ORDER BY CAST(SUBSTRING_INDEX(day_maximum_count_code, 'code', -1) AS UNSIGNED))
LIMIT #{currentFirstIndex},#{rowPerPage};

/* 데이터 개수 조회 */
SELECT
	COUNT(*)
FROM
	${tableName}

	
		day_maximum_count_code LIKE'%point%';

/* 컬럼 내 중복 제거된 데이터 조회 */
SELECT
	
	DISTINCT ${columnName} AS distinctData
	
	
	
		DISTINCT CASE 
		   WHEN code_use = 'Y' THEN '사용가능'
		   WHEN code_use = 'N' THEN '사용불가능'
		END  AS distinctData
	
FROM
	${tableName}

	
		day_maximum_count_code LIKE'%point%';
