mapper.select:
SELECT appi.approval_partner_plus_info_code AS partnerCode, appi.partner_name AS partnerName FROM approval_partner_plus_info AS appi
WHERE appi.user_id = #{loginId}
WHERE appi.partner_type = '설치 사업장' ORDER BY CAST(SUBSTRING_INDEX(approval_partner_plus_info_code,'business',-1) AS UNSIGNED)

SELECT ikl.kiosk_serial_num as kioskSerialNum FROM approval_partner_plus_info AS appi INNER join user_manage AS um ON appi.user_id=um.user_id INNER join installed_kiosk_list AS ikl ON ikl.approval_partner_plus_info_code = appi.approval_partner_plus_info_code
WHERE um.user_id =#{loginId}
ORDER BY CAST(SUBSTRING_INDEX(kioskSerialNum,'num',-1) AS UNSIGNED)
